mapper.select:
SELECT a.*, category.id AS b_category_id, category.`name` AS b_category_name, category.description AS b_category_description, l.lookCount AS look_count, c.commentCount AS comment_count, love.loveCount AS love_count FROM biz_article a INNER JOIN biz_category category ON a.category_id = category.id INNER JOIN biz_article_tags atag ON a.id = atag.article_id LEFT JOIN ( SELECT l.article_id, IFNULL(count(1), 0) AS lookCount FROM biz_article_look l GROUP BY l.article_id ) l ON a.id = l.article_id LEFT JOIN ( SELECT sid, IFNULL(count(1), 0) AS commentCount FROM biz_comment c WHERE c.status = 1 GROUP BY sid ) c ON a.id = c.sid LEFT JOIN ( SELECT l.biz_id, IFNULL(count(1), 0) AS loveCount FROM biz_love l WHERE l.biz_type=1 and l.status=1 GROUP BY l.biz_id ) love ON a.id = love.biz_id WHERE 1 = 1
AND a.category_id = #{categoryId}
AND atag.tag_id IN (
#{tagId}
)
AND atag.tag_id = #{tagId}
AND a.top = #{top}
AND a.status = #{status}
AND a.recommended = #{recommended}
AND a.slider = #{slider}
AND ( a.title LIKE CONCAT('%',#{keywords , jdbcType=VARCHAR},'%') OR a.content LIKE CONCAT('%',#{keywords , jdbcType=VARCHAR},'%') OR a.description LIKE CONCAT('%',#{keywords , jdbcType=VARCHAR},'%') OR a.keywords LIKE CONCAT('%',#{keywords , jdbcType=VARCHAR},'%') )
GROUP BY a.id
ORDER BY RAND()
ORDER BY
a.slider DESC,
a.top DESC,
a.create_time DESC

SELECT a.*, t.id AS tag_id, t.`name` AS tag_name, t.description AS tag_description FROM biz_article a INNER JOIN biz_article_tags atag ON a.id = atag.article_id INNER JOIN biz_tags t ON atag.tag_id = t.id WHERE a.id IN (
#{id}
)
